import __polyfill from "babel-polyfill";
import should from "should";
import Stream from 'stream';
import path from 'path';
import fs from 'fs';
import formstream from 'formstream';
import busboy from '../isotropy-busboy';

describe('isotropy-busboy', () => {
  it('should gather all fields and streams', async () => {
    const req = request();
    const parts = await busboy(req);
    parts.length.should.equal(7);
  });

  it('should work with array fields', async () => {
    const req = request();
    const parts = await busboy(req);
    parts[3].val[0].should.equal("value1");
  });

  it('should have three file streams', async () => {
    const req = request();
    const parts = await busboy(req);
    const numStreams = parts.filter(p => p instanceof Stream).length;
    numStreams.should.equal(3);
  });
})

